import React, { Component } from 'react'

export default class Country extends Component {
    state={
        CountriesDataRequested:"",
        loading:true,
        selectedCountry:''

    }

    // Prototype methods, Bind in Constructor (ES2015)
    handleEvent=(event)=> {

        this.setState({
            ...this.state,
            selectedCountry:event.target.value
        })
    }

    render() {
        console.log(this.props.datas)
        let selected=[];
        for (let x =0; x<this.props.datas.length;x++){

                Object.keys(this.props.datas[x]).map((keys,i)=>{
                    
                    if(keys==="Country")
                    {
                        return selected[x]=<option key={x} value={keys}>{this.props.datas[x][keys]}</option> 
                    }
                })
            
        }
        return (
            <div className="row">
            <select onChange={this.handleEvent}style={{"width":"100%","height":"50px"}}>
                <option value="">-Select the prefered data-</option>
                {selected}
            </select>
        </div>
        )
    }
}
