import React, { Component } from 'react'
import Aux from '../../HOC/Auxulary'
import Card from '../Cards/card/card'

export default class Country extends Component {
    state={
        CountriesDatanumber:"",
        loading:true,
        selectedCountry:''

    }

    // Prototype methods, Bind in Constructor (ES2015)
    handleEvent=(event)=> {
        Object.keys(event.target.value).map((keys,i)=>{
                    
            if(keys==="Country")
            {
                selected[x]=<option key={x} value={{"value":this.props.datas[x][keys],"number":x}}>{this.props.datas[x][keys]}</option> 
            }
        })
        this.setState({
            ...this.state,
            selectedCountry:event.target.value["value"],
            CountriesDatanumber:event.target.value["number"]
        })
    }

    render() {
        let selected=[];
        let printinfTheDatas=[]
        for (let x =0; x<this.props.datas.length;x++){

                Object.keys(this.props.datas[x]).map((keys,i)=>{
                    
                    if(keys==="Country")
                    {
                        selected[x]=<option key={x} value={{"value":this.props.datas[x][keys],"number":x}}>{this.props.datas[x][keys]}</option> 
                    }
                })
            
        }

        if (this.state.selectedCountry!=='')
        {
        for (let x =0; x<this.props.datas.length;x++){
            Object.keys(this.props.datas[x]).map((keys,i)=>{
                    
                if(this.props.datas[x][keys]===this.state.selectedCountry)
                {
                    printinfTheDatas[i]=<Card status={keys} number={this.props.datas[x][keys]}></Card>
                }
            })
        }

        }
        return (
            <Aux>
            <div className="row">
            <select onChange={this.handleEvent}style={{"width":"100%","height":"50px"}}>
                <option value="">-Select the prefered data-</option>
                {selected}
            </select>
        </div>
        <div className="row">
                {printinfTheDatas}
             </div>
            </Aux>
            
        )
    }
}
