import React, { Component } from 'react';
import './App.css';
import Axios from 'axios'
import Header from './Header/Header';
import {Switch,Route} from 'react-router-dom'
import Global from './Body/Global/Global'
import Country from './Body/Countries/Country'


class App extends Component{

  state={
    data:{},
    countriesData:{}
  }
  componentDidMount() {
      Axios.get('https://api.covid19api.com/summary')
        .then(response=>{

            this.setState({
                ...this.state,
                data:response.data['Global'],
                countriesData:response.data['countries']
            })
        })
  }

  render(){
    return(
      <div className="App">
      <Header></Header>
      <Switch>

        <Route path="/global" exact render={()=><Global datas={this.state.data}/>}/>
        <Route path="/country" exact componenet={Country}/>
        </Switch>
      </div>
    )
  }


}

export default App;
