import React, { Component } from 'react'
import Card from './card/card'
import Axios from 'axios'
import Cards from './Cards/Cards'
import Aux from '../HOC/Aux'

export default class Body extends Component {

    state={
        data:{},
        dataRequested:""

    }
    componentDidMount() {

        Axios.get('https://api.covid19api.com/summary')
        .then(response=>{
           
            this.setState({
                ...this.state,
                data:response.data['Global']
            })
        })
        
    }
    // Prototype methods, Bind in Constructor (ES2015)
    handleEvent=(event)=> {
        this.setState({
            ...this.state,
            dataRequested:event.target.value
        })
    }


    render() {

        let selected=[]
        Object.keys(this.state.datas).map((keys,i)=>{
            console.log(keys)
                selected[i]=<option key={key} value={this.state.datas}></option>
        })

        return (
            <Aux>
                <div className="row">
                    <select onChange={this.handleEvent}>
                        <option disabled selected>-Select the prefered data-</option>

                    </select>
                </div>
            <div>
                <Cards datas={this.state.data}></Cards>
            </div>
            </Aux>
        )
    }
}
